import React from 'react'
import { Play } from '../Alies'
import { BallPosition,PossessionTeam,BallPlace, Punt,EndMakeDrive, GetPoints,Initializer,KickOff, DownDistance,InterceptionFunc } from './EntryPoint'


type Props = {
    data:Play
    ballPlace:boolean
    ballPossession:boolean
    ballOn:number
    down:number
    distance:number
    setBallPlace:React.Dispatch<React.SetStateAction<boolean>>
    setBallPossession:React.Dispatch<React.SetStateAction<boolean>>
    setBallOn:React.Dispatch<React.SetStateAction<number>>
    setId:React.Dispatch<React.SetStateAction<number>>
    setDown:React.Dispatch<React.SetStateAction<number>>
    setDistance:React.Dispatch<React.SetStateAction<number>>
    setHomePoints:React.Dispatch<React.SetStateAction<number>>
    setAwayPoints:React.Dispatch<React.SetStateAction<number>>
}

const Allprocessor:React.FC<Props> = ({data,ballPlace,ballPossession,ballOn,down,distance,setBallPlace,setBallPossession,setBallOn,setId,setDown,setDistance,setHomePoints,setAwayPoints}) => {

    var ballPossessionResult :boolean = ballPossession

    // BallOnのヤードに関して、Offenceでgainした時の処理
    BallPosition({data,ballPlace,ballPossession,setBallOn,ballOn,setBallPlace})
    // Ballがどちらの陣地にあるかに関する処理
    BallPlace(data,ballPlace)
    // Puntの時の処理
    if (data.o_or_k === "Punt"){
        EndMakeDrive({data,ballPossession,setBallPossession,setId,setDown,setDistance})
        Punt({data,ballPossession,ballOn,setBallOn,ballPlace,setBallPlace})
    }
    // FGの処理// TDの処理
    if (data.kick_isgood === "Good" || ballOn<0){
        GetPoints({data,ballPossession,setHomePoints,setAwayPoints,ballOn,setBallOn})
        EndMakeDrive({data,ballPossession,setBallPossession,setId,setDown,setDistance})
        setBallPlace(!ballPlace)
    }else if(data.o_or_k==="FG" && data.kick_isgood==="No Good"){
        EndMakeDrive({data,ballPossession,setBallPossession,setId,setDown,setDistance})
    }
    // Kick Off処理
    if (data.o_or_k ==="Kick Off"){
        KickOff({data,ballOn,setBallOn,ballPlace,setBallPlace})
        setBallPossession(!ballPossession)
    }

    // INTの処理//Fumbleの処理　=> TurnOver,BallPlace,interceptされた場所、FumbelRecoverされた場所,return_yards
    // INT=> Pass=>INT=>INT_PLACE=>RETURN_YARDS=>TURNOVER(BALLPOSSESSIONの変化)
    if (data.pass_iscomplete==="Interception" || data.result === "Fumble"){
        setBallPossession(!ballPossession)
        InterceptionFunc({data,ballPlace,ballPossession,setBallPlace,setBallPossession,setBallOn});
        EndMakeDrive({data,ballPossession,setBallPossession,setId,setDown,setDistance})
    }




    // D&Dの処理,シンプルにできる
    if (data.o_or_k == "Offense"){
        if(down === 4 && distance > data.yards_gained){
            EndMakeDrive({data,ballPossession,setBallPossession,setId,setDown,setDistance})
            setBallPossession(!ballPossession)
            console.log("Gamble failed")
        }else{
            DownDistance({data,distance,setDown,down,setDistance})
        }
    }

    // ボール保持チームが変わった時の処理
    PossessionTeam(data,ballPossession);

  return null
}

export default Allprocessor
