import React from 'react'
import { Play } from '../Alies'
import { Props } from './Props'

type Props2 =  {
    data:Play
    setBallOn:React.Dispatch<React.SetStateAction<number>>
    ballOn:number
    ballPossession:boolean
    ballPlace:boolean
    setBallPlace:React.Dispatch<React.SetStateAction<boolean>>
    setBallPossession:React.Dispatch<React.SetStateAction<boolean>>
}


const Punt:React.FC<Props2> = ({data,ballOn,setBallOn,ballPossession,setBallPossession,ballPlace,setBallPlace}) => {

    if(data.kick_result==="TB"){
        setBallOn(25)
    }else if(ballOn+data.punt_distance-data.return_yards<=50){
        setBallOn(ballOn+data.punt_distance-data.return_yards)
    }else{
        setBallOn(100 - (ballOn+data.punt_distance-data.return_yards))
        setBallPlace(!ballPlace)
    }
    setBallPossession(!ballPossession)


    fetch("http://localhost:9091/drives",{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body: JSON.stringify({})
        }).then(() => {
                console.log("drive") 
        })

  return null
}

export default Punt

