import React from 'react'
import { Play } from '../Alies'

type Props2 = {
    data:Play
    ballPossession:boolean
    setBallPossession:React.Dispatch<React.SetStateAction<boolean>>
    setId:React.Dispatch<React.SetStateAction<number>>
}

const EndMakeDrive:React.FC<Props2> = ({data,ballPossession,setBallPossession,setId}) => {

    if (data.o_or_k==="Punt"){
        setBallPossession(!ballPossession)
    }

    setId(prevId => prevId + 1)

    fetch("http://localhost:9091/drives",{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body: JSON.stringify({})
        }).then(() => {
                console.log("drive") 
        })

  return null
}

export default EndMakeDrive

// PossessionTeamを変える
