import React,{useState,useEffect} from 'react'
import './style.css'
import {BallTeam,OorK,PlayType,Passer,Runner,Result,Tackler,GainYards} from './EntryPoint'
import { Props } from './Alies'



const Index:React.FC = () => {


    const [ballTeam,setBallTeam] = useState<string>("");
    const [situation,setSituation] = useState<string>("");
    const [playType,setPlayType] = useState<string>("");
    const [passer,setPasser] = useState<string>("");
    const [runner,setRunner] = useState<string>("");
    const [result,setResult] = useState<string>("");
    const [tackler,setTackler] = useState<string>("")
    const [gainYards,setGainYards] = useState<string>("");

    var i:number = 1
    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {

        e.preventDefault();

        const playInfo  = {
        drive_id        :i,
        ball_possession :ballTeam,
        o_or_k          :situation,
        play_type       :playType,
        passer          :passer,
        runner          :runner,
        result          :result,
        tackler         :tackler,
        yards_gained    :gainYards
        }

        fetch("http://localhost:9091/plays",{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body: JSON.stringify(playInfo)
        }).then(() => {
            console.log("success!")
        })

        if (situation === "Punt" || situation === "FG"|| situation === "TFP"){
            i ++;
            fetch("http://localhost:9091/drives",{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body: JSON.stringify({})
        }).then(() => {
            console.log("drive!")
        })
        }

        console.log(playInfo);
    }

  return (
    <div className="">
        <div  className="bg-dark text-white d-flex justify-content-center">
            <h1>Football Game Cast</h1>
        </div>
        <div className='m-4'>
            <div className="mt-4">
                <div>
                    <h4>Play記録フォーム</h4>
                </div>
                <form onSubmit={(e) => handleSubmit(e)}>
                    <BallTeam setBallTeam={setBallTeam}/>
                    <OorK setSituation={setSituation}/>
                    <PlayType setPlayType={setPlayType} />
                    <Passer setPasser ={setPasser} />
                    <Runner setRunner = {setRunner}/>
                    <Result setResult = {setResult} />
                    <Tackler setTackler = {setTackler} />
                    <GainYards setGainYards={setGainYards} />
                    <div className='mt-2'>
                        <button type='submit' className='btn btn-lg btn-primary'>完了</button>
                    </div>
                </form>
            </div>
        </div>

    </div>
  )
}

export default Index
