import React from "react"
import { Play } from "../Alies"

type Props = {
    data:Play;
    ballPossession:boolean
    ballPlace:boolean
    ballOn:number
    gainYards:number
    setBallOn:React.Dispatch<React.SetStateAction<number>>
    setBallPlace:React.Dispatch<React.SetStateAction<boolean>>
}

const SetYards:React.FC<Props> = ({data,ballPossession,ballPlace,ballOn,gainYards,setBallOn,setBallPlace}) => {

    if (possession && ballPlace){
        if (ballOn + gainYards <= 50){
            setBallOn(prevBallOn => prevBallOn + gainYards)
            data.ball_on_result = ballOn + gainYards
        }else{
            setBallOn(prevBallOn => 100 - prevBallOn - gainYards)
            data.ball_on_result = 100 - ballOn - gainYards
            setBallPlace(!ballPlace)
        }
    }
    else if (possession && (!ballPlace)){
        setBallOn(prevBallOn => prevBallOn - gainYards)
        data.ball_on_result = ballOn - gainYards
    }else if ((!possession) && ballPlace){
        setBallOn(prevBallOn => prevBallOn - gainYards)
        data.ball_on_result = ballOn - gainYards
    }else{
        if (ballOn + gainYards <= 50){
            setBallOn(prevBallOn => prevBallOn + gainYards)
            data.ball_on_result = ballOn + gainYards
        }else{
            setBallOn(prevBallOn => 100 - prevBallOn - gainYards)
            data.ball_on_result = 100 - ballOn - gainYards
            setBallPlace(!ballPlace)
        }
    }
    console.log(100)
    console.log(gainYards)
    console.log(ballOn)
  return null
}

export default SetYards
