import React,{useState,useEffect} from 'react'
import {useForm} from "react-hook-form"
import './style.css'
import {BallTeam,OorK,PlayType,Passer,Runner,Result,Tackler,GainYards,Reciever,PassIsComplete,Kicker,KickIsGood,KickResult,Punter,ReturnYards, TOReturnYards,IntPlace,FumbleRecPlace, KickDistance,PuntDistance,Interception} from './EntryPoint'
import { Play } from './Alies'
import Returner from './components/Returner'
import { BallPosition,PossessionTeam,BallPlace, Punt,EndMakeDrive, GetPoints,Initializer,KickOff, DownDistance,InterceptionFunc } from './functions/EntryPoint'



var playAmount = 0

const Index:React.FC = () => {

    const { register,handleSubmit,watch } = useForm<Play>();
    const [id,setId] = useState<number>(1);
    const [ballOn,setBallOn]=useState<number>(35);
    const [ballPossession,setBallPossession]=useState<boolean>(true);
    const [ballPlace,setBallPlace]=useState<boolean>(true);
    const [homePoints,setHomePoints] = useState<number>(0);
    const [awayPoints,setAwayPoints] = useState<number>(0);
    const [down,setDown] = useState<number>(1);
    const [distance,setDistance] = useState<number>(10);
    console.log(watch().pass_iscomplete)


  return (
    <div className="">
        <div  className="bg-dark text-white d-flex justify-content-center">
            <h1>Football Game Cast</h1>
        </div>
        <div className='m-4'>
            <div className="mt-4">
                <div>
                    <h4>Play記録フォーム</h4>
                </div>
                <form onSubmit={handleSubmit((data) => {
                    Initializer(playAmount);
                    // ボール保持チームが変わった時の処理
                    PossessionTeam(data,ballPossession);
                    // BallOnのヤードに関して、Offenceでgainした時の処理
                    BallPosition({data,ballPlace,ballPossession,setBallOn,ballOn,setBallPlace})
                    // Ballがどちらの陣地にあるかに関する処理
                    BallPlace(data,ballPlace)
                    // Puntの時の処理
                    if (data.o_or_k === "Punt"){
                        EndMakeDrive({data,ballPossession,setBallPossession,setId,setDown,setDistance})
                        Punt({data,ballOn,setBallOn,ballPlace,setBallPlace})
                    }
                    // FGの処理// TDの処理
                    if (data.kick_isgood === "Good" || ballOn<0){
                        GetPoints({data,ballPossession,setHomePoints,setAwayPoints,ballOn,setBallOn})
                        EndMakeDrive({data,ballPossession,setBallPossession,setId,setDown,setDistance})
                        setBallPlace(!ballPlace)
                    }else if(data.o_or_k==="FG" && data.kick_isgood==="No Good"){
                        EndMakeDrive({data,ballPossession,setBallPossession,setId,setDown,setDistance})
                    }
                    // Kick Off処理
                    if (data.o_or_k ==="Kick Off"){
                        KickOff({data,ballOn,setBallOn,ballPlace,setBallPlace})
                        setBallPossession(!ballPossession)
                    }

                    // INTの処理//Fumbleの処理　=> TurnOver,BallPlace,interceptされた場所、FumbelRecoverされた場所,return_yards
                    // INT=> Pass=>INT=>INT_PLACE=>RETURN_YARDS=>TURNOVER(BALLPOSSESSIONの変化)
                    if (data.pass_iscomplete==="Interception"){
                        InterceptionFunc({data,ballPlace,ballPossession,setBallPlace,setBallPossession,setBallOn});
                    }




                    // D&Dの処理,シンプルにできる
                    if (data.o_or_k == "Offense"){
                        if(down === 4 && distance > data.yards_gained){
                            EndMakeDrive({data,ballPossession,setBallPossession,setId,setDown,setDistance})
                            setBallPossession(!ballPossession)
                            console.log(1)
                        }else{
                            DownDistance({data,distance,setDown,down,setDistance})
                        }
                    }


                    console.log(ballOn)

                    playAmount++;
                    data.drive_id = id
                    data.ball_on = ballOn
                    data.hometeam_points = homePoints
                    data.awayteam_points = awayPoints
                    data.down = down
                    data.distance = distance

                    fetch("http://localhost:9091/plays",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify(data)
                    }).then(() => {
                        console.log("success!")
                        console.log(data.ball_on)
                    })

                    if ( data.o_or_k === "FG"|| data.o_or_k === "TFP"){
                        setId(prevId => prevId + 1)
                        fetch("http://localhost:9091/drives",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify({})
                    }).then(() => {
                        console.log("drive!")

                    })
                    }
                })}>
                    <BallTeam register={register}/>
                    <OorK register={register}/>
                    <PlayType register={register} value={watch().o_or_k}/>
                    <Passer register={register}  value={watch().play_type}/>
                    <Reciever register={register} value={watch().play_type}/>
                    <PassIsComplete register={register} value={watch().play_type}/>
                    <IntPlace register={register} value={watch().pass_iscomplete}/>
                    <Interception register={register} value={watch().pass_iscomplete}/>
                    <Runner register={register} value={watch().play_type} value2={watch().o_or_k}/>
                    <Result register={register} value={watch().o_or_k}/>
                    <FumbleRecPlace register={register} value={watch().result}/>
                    <TOReturnYards register={register} value={watch().result}/>
                    <Tackler register={register}/>
                    <GainYards register={register} value={watch().o_or_k}/>
                    <Kicker register={register} value={watch().o_or_k}/>
                    <KickDistance register={register} value={watch().o_or_k}/>
                    <KickIsGood register={register} value={watch().o_or_k}/>
                    <KickResult register={register} value={watch().o_or_k}/>
                    <Punter register={register} value={watch().o_or_k}/>
                    <PuntDistance register={register} value={watch().o_or_k}/>
                    <ReturnYards register={register} value={watch().o_or_k}/>
                    <Returner register={register} value={watch().o_or_k}/>
                    <div className='mt-2'>
                        <button type='submit' className='btn btn-lg btn-primary'>完了</button>
                    </div>
                </form>
            </div>
        </div>

    </div>
  )
}

export default Index
