import React from "react"
import { Play } from "../Alies"
import { Props } from "./Props"


const ballPosition:React.FC<Props> = ({data,ballPlace,ballPlaceResult,ballPossession,setBallOn,ballOn,setBallPlace}) => {
    if (ballPlace &&  ballPossession){
        if (ballOn + data.yards_gained <= 50){
            setBallOn(prevBallOn => prevBallOn + data.yards_gained)
            data.ball_on_result = ballOn + data.yards_gained
        }else{
            setBallOn(prevBallOn => 100 - prevBallOn -   data.yards_gained)
            data.ball_on_result = 100 - ballOn -   data.yards_gained
            setBallPlace(!ballPlace)
            ballPlaceResult = !ballPlaceResult
            console.log(ballPlaceResult,10)
        }
    }
    else if ((!ballPlace) && ballPossession){
        setBallOn(prevBallOn => prevBallOn - data.yards_gained)
        data.ball_on_result = ballOn - data.yards_gained
    }
    else if ((!ballPlace) && (!ballPossession)){
        if (ballOn + data.yards_gained <= 50){
            setBallOn(prevBallOn => prevBallOn + data.yards_gained)
            data.ball_on_result = ballOn + data.yards_gained
        }else{
            setBallOn(prevBallOn => 100 - prevBallOn - data.yards_gained)
            data.ball_on_result = 100 - ballOn -   data.yards_gained
            setBallPlace(!ballPlace)
            ballPlaceResult = !ballPlaceResult
        }
    }
    else if (ballPlace && (!ballPossession)){
        setBallOn(prevBallOn => prevBallOn - data.yards_gained)
        data.ball_on_result = ballOn - data.yards_gained
    }

    console.log(ballPlaceResult,20)
    return null
}

export default ballPosition
