import React,{useState,useEffect} from 'react'
import {useForm} from "react-hook-form"
import './style.css'
import {BallTeam,OorK,PlayType,Passer,Runner,Result,Tackler,GainYards,Reciever,PassIsComplete,Kicker,KickIsGood,KickResult,Punter,ReturnYards, TOReturnYards, KickDistance,PuntDistance,Interception} from './EntryPoint'
import { Play } from './Alies'
import Returner from './components/Returner'
import { Initializer,AllProcessor } from './functions/EntryPoint'



var playAmount = 0

const Index:React.FC = () => {

    const { register,handleSubmit,watch } = useForm<Play>();
    const [id,setId] = useState<number>(1);
    const [ballOn,setBallOn]=useState<number>(35);
    const [ballPossession,setBallPossession]=useState<boolean>(true);
    const [ballPlace,setBallPlace]=useState<boolean>(true);
    const [homePoints,setHomePoints] = useState<number>(0);
    const [awayPoints,setAwayPoints] = useState<number>(0);
    const [down,setDown] = useState<number>(1);
    const [distance,setDistance] = useState<number>(10);



    type DepthChartsUrls = {
        teamName:string
        url:string
    }
    type TeamInfo = {
        teamName:string
        defensePositions:Position[]
        offensePositions:Position[]
    }

    type Position = {     
        positionName:string    
        athletes:Athlete[]
        // atheletes:Athlete[]
    }
    type Athlete = {
        athleteUrl:string
        fullName:string
        jersey:string
    }

    var depthChartsUrls:DepthChartsUrls[] = []
    var teamInfo:TeamInfo[] = []
    var defensePositions:Position[] = []
    var offensePositions:Position[] = []
    var athletesList:Athlete[] = []


    useEffect(() => {
        const f = async () => {
            for (let i = 1; i<31; i++){
                await fetch('http://sports.core.api.espn.com/v2/sports/football/leagues/nfl/seasons/2022/teams/'+i+'?lang=en&region=us', {
              method: 'GET'})
                .then(res => res.json())
                .then(data => {
                    depthChartsUrls.push({
                        teamName:data["displayName"],
                        url:data["depthCharts"]["$ref"]         
                })
            })}
            await fetch('http://sports.core.api.espn.com/v2/sports/football/leagues/nfl/seasons/2022/teams/'+33+'?lang=en&region=us', {
              method: 'GET'})
                .then(res => res.json())
                .then(data => {
                    depthChartsUrls.push({
                        teamName:data["displayName"],
                        url:data["depthCharts"]["$ref"]
                })
            })
            await fetch('http://sports.core.api.espn.com/v2/sports/football/leagues/nfl/seasons/2022/teams/'+34+'?lang=en&region=us', {
              method: 'GET'})
                .then(res => res.json())
                .then(data => {
                    depthChartsUrls.push({
                        teamName:data["displayName"],
                        url:data["depthCharts"]["$ref"]
                })
            })
            for (let i=0;i<depthChartsUrls.length;i++){
                await fetch(depthChartsUrls[i].url, {method: 'GET'})
                .then(res => res.json())
                .then(async data => {

            for (let key in data["items"][0]["positions"]) {
                for (var value of data["items"][0]["positions"][key]["athletes"]) {

                    await fetch(value["athlete"]["$ref"],{method:'GET'}).then(res => res.json()).then(async playerData => {
                        await athletesList.push({
                            athleteUrl:value["athlete"]["$ref"],
                            fullName:playerData["displayName"],
                            jersey:playerData["jersey"]
                        })
                    })       
                }      
                await defensePositions.push({
                    positionName:data["items"][0]["positions"][key]["position"]["name"],
                    athletes : athletesList
                })
                athletesList = []
            }

            for (let key in data["items"][2]["positions"]) {
                for (var value of data["items"][2]["positions"][key]["athletes"]) {
                    await fetch(value["athlete"]["$ref"],{method:'GET'}).then(res => res.json()).then(async playerData => {
                        await athletesList.push({
                            athleteUrl:value["athlete"]["$ref"],
                            fullName:playerData["displayName"],
                            jersey:playerData["jersey"]
                        })
                    })    
                } 
                await offensePositions.push({
                    positionName:data["items"][2]["positions"][key]["position"]["name"],
                    athletes : athletesList
                })
                athletesList = []
            }
                teamInfo.push({
                    teamName:depthChartsUrls[i].teamName,
                    defensePositions:defensePositions,
                    offensePositions:offensePositions
                })
                defensePositions = []
                offensePositions = []
                })               
            }
            console.log(teamInfo)
        };
        f();
      }, []);


  return (
    <div className="">
        <div  className="bg-dark text-white d-flex justify-content-center">
            <h1>Football Game Cast</h1>
        </div>
        <div className='m-4'>
            <div className="mt-4">
                <div>
                    <h4>Play記録フォーム</h4>
                </div>
                <form onSubmit={handleSubmit((data) => {


                    AllProcessor({data,ballPlace,ballPossession,ballOn,down,distance,setBallPlace,setBallPossession,setBallOn,setId,setDown,setDistance,setHomePoints,setAwayPoints})

                    Initializer(playAmount);

                    console.log(data.ball_place_result)

                    playAmount++;
                    data.drive_id = id
                    data.ball_on = ballOn
                    data.hometeam_points = homePoints
                    data.awayteam_points = awayPoints
                    data.down = down
                    data.distance = distance

                    fetch("http://localhost:9091/plays",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify(data)
                    }).then(() => {
                        console.log("success!")
                        console.log(data.ball_on)  
                        console.log(data.ball_on_result)
                    })

                    if ( data.o_or_k === "FG"|| data.o_or_k === "TFP"){
                        setId(prevId => prevId + 1)
                        fetch("http://localhost:9091/drives",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify({})
                    }).then(() => {
                        console.log("drive!")

                    })
                    }
                })}>
                    <BallTeam register={register}/>
                    <OorK register={register}/>
                    <PlayType register={register} value={watch()}/>
                    <Passer register={register}  value={watch()}/>
                    <PassIsComplete register={register} value={watch()}/>
                    <Reciever register={register} value={watch()}/>
                    <Runner register={register} value={watch()}/>
                    <GainYards register={register} value={watch()}/>
                    <Result register={register} value={watch()}/>
                    <Interception register={register} value={watch()}/>
                    <Kicker register={register} value={watch()}/>
                    <Punter register={register} value={watch()}/>
                    <KickDistance register={register} value={watch()}/>
                    <PuntDistance register={register} value={watch()}/>
                    <ReturnYards register={register} value={watch()}/>
                    <KickIsGood register={register} value={watch()}/>
                    <KickResult register={register} value={watch()}/>
                    <Returner register={register} value={watch()}/>
                    <Tackler register={register}/>
                    <TOReturnYards register={register} value={watch()}/>
                    <div className='mt-2'>
                        <button type='submit' className='btn btn-lg btn-primary'>完了</button>
                    </div>
                </form>
            </div>
        </div>

    </div>
  )
}

export default Index
