import React from 'react'
import { Play } from '../Alies'
import SetYards from './SetYards'


type Props =  {
    data:Play
    ballPossession:boolean
    setBallOn:React.Dispatch<React.SetStateAction<number>>
    ballOn:number
    ballPlace:boolean
    setBallPlace:React.Dispatch<React.SetStateAction<boolean>>
}


const Punt:React.FC<Props> = ({data,ballPossession,ballOn,setBallOn,ballPlace,setBallPlace}) => {

    if(data.kick_result==="TB"){
        setBallOn(20)
    }
    else{
        SetYards(ballPossession,ballPlace,ballOn,data.punt_distance,setBallOn,setBallPlace)
    }
