import React,{useState,useEffect} from 'react'
import {useForm} from "react-hook-form"
import './style.css'
import {BallTeam,OorK,PlayType,Passer,Runner,Result,Tackler,GainYards,Reciever,PassIsComplete,Kicker,KickIsGood,KickResult,Punter,ReturnYards,} from './EntryPoint'
import { Play } from './Alies'
import YardsRecovered from './components/YardsRecovered'
import Returner from './components/Returner'

var playAmount = 0

const Index:React.FC = () => {

    const { register,handleSubmit,watch } = useForm<Play>();
    const [id,setId] = useState<number>(1);
    const [ballOn,setBallOn]=useState<number>(35);
    const [ballPossession,setBallPossession]=useState<boolean>(true);
    const [ballPlace,setBallPlace]=useState<boolean>(true);
    console.log(watch())


  return (
    <div className="">
        <div  className="bg-dark text-white d-flex justify-content-center">
            <h1>Football Game Cast</h1>
        </div>
        <div className='m-4'>
            <div className="mt-4">
                <div>
                    <h4>Play記録フォーム</h4>
                </div>
                <form onSubmit={handleSubmit((data) => {

                    console.log(data.yards_gained)
                    if (playAmount === 0){
                        fetch("http://localhost:9091/drives",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify({})
                    }).then(() => {
                        console.log("Post initial drive!")
                    })
                    }

                    playAmount++;
                    data.drive_id = id   


                    fetch("http://localhost:9091/plays",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify(data)
                    }).then(() => {
                        console.log("success!")
                        console.log(data.ball_on)
                    })

                    if (data.o_or_k === "Punt" || data.o_or_k === "FG"|| data.o_or_k === "TFP"){
                        setId(prevId => prevId + 1)
                        fetch("http://localhost:9091/drives",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify({})
                    }).then(() => {
                        console.log("drive!")

                    })
                    }
                })}>
                    <BallTeam register={register}/>
                    <OorK register={register}/>
                    <PlayType register={register} value={watch().o_or_k}/>
                    <Passer register={register}  value={watch().play_type}/>
                    <Reciever register={register} value={watch().play_type}/>
                    <PassIsComplete register={register} value={watch().play_type}/>
                    <Runner register={register} value={watch().play_type} value2={watch().o_or_k}/>
                    <Result register={register} value={watch().o_or_k}/>
                    <Tackler register={register}/>
                    <GainYards register={register}/>
                    <Kicker register={register} value={watch().o_or_k}/>
                    <KickIsGood register={register} value={watch().o_or_k}/>
                    <KickResult register={register} value={watch().o_or_k}/>
                    <Punter register={register} value={watch().o_or_k}/>
                    <ReturnYards register={register} value={watch().o_or_k}/>
                    <YardsRecovered register={register} value={watch().o_or_k}/>
                    <Returner register={register} value={watch().o_or_k}/>
                    <div className='mt-2'>
                        <button type='submit' className='btn btn-lg btn-primary'>完了</button>
                    </div>
                </form>
            </div>
        </div>

    </div>
  )
}

export default Index
