import React from "react"
import { Play } from "../Alies"


type Props = {
    data:Play
    ballPlace:boolean
    ballPossession:boolean
    setBallOn:React.Dispatch<React.SetStateAction<number>>
    ballOn:number
    setBallPlace:React.Dispatch<React.SetStateAction<boolean>>
}


const ballPosition:React.FC<Props> = ({data,ballPlace,ballPossession,setBallOn,ballOn,setBallPlace}) => {
    if (ballPlace &&  ballPossession){
        if (ballOn + data.yards_gained <= 50){
            setBallOn(prevBallOn => prevBallOn + data.yards_gained)
        }else{
            setBallOn(prevBallOn => 100 - prevBallOn -   data.yards_gained)
            setBallPlace(false)
        }
    }
    else if (!ballPlace && ballPossession){
        setBallOn(prevBallOn => prevBallOn - data.yards_gained)
    }else if (!ballPlace &&  !ballPossession){
        if (ballOn + data.yards_gained <= 50){
            setBallOn(prevBallOn => prevBallOn + data.yards_gained)
        }else{
            setBallOn(prevBallOn => 100 - prevBallOn -   data.yards_gained)
            setBallPlace(false)
        }
    }
    else if (ballPlace && !ballPossession){
        setBallOn(prevBallOn => prevBallOn - data.yards_gained)
    }
    return null
}

export default ballPosition
