import React,{useState,useEffect} from 'react'
import {useForm} from "react-hook-form"
import './style.css'
import {BallTeam,OorK,PlayType,Passer,Runner,Result,Tackler,GainYards} from './EntryPoint'
import { Play } from './Alies'

var playAmount = 0

const Index:React.FC = () => {

    const { register,handleSubmit,watch } = useForm<Play>();
    const [id,setId] = useState<number>(1);
    console.log(watch())


  return (
    <div className="">
        <div  className="bg-dark text-white d-flex justify-content-center">
            <h1>Football Game Cast</h1>
        </div>
        <div className='m-4'>
            <div className="mt-4">
                <div>
                    <h4>Play記録フォーム</h4>
                </div>
                <form onSubmit={handleSubmit((data) => {

                    if (playAmount === 0){
                        fetch("http://localhost:9091/drives",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify({})
                    }).then(() => {
                        console.log("Post initial drive!")
                    })
                    }

                    playAmount++;
                    data.drive_id = id   

                    fetch("http://localhost:9091/plays",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify(data)
                    }).then(() => {
                        console.log("success!")
                        console.log(typeof data["yards_gained"])
                    })

                    if (data.o_or_k === "Punt" || data.o_or_k === "FG"|| data.o_or_k === "TFP"){
                        setId(prevId => prevId + 1)
                        fetch("http://localhost:9091/drives",{
                        method:"POST",
                        headers:{"Content-Type":"application/json"},
                        body: JSON.stringify({})
                    }).then(() => {
                        console.log("drive!")
                    })
                    }
                })}>
                    <div><BallTeam register={register}/></div>
                    <div><OorK register={register}/></div>
                    <div><PlayType register={register}/></div>
                    <div><Passer register={register}  value={watch().play_type}/></div>
                    <div><Runner register={register}/></div>
                    <div><Result register={register}/></div>
                    <div><Tackler register={register}/></div>
                    <div><GainYards register={register}/></div>
                    <div className='mt-2'>
                        <button type='submit' className='btn btn-lg btn-primary'>完了</button>
                    </div>
                </form>
            </div>
        </div>

    </div>
  )
}

export default Index
