import React,{useEffect, useState} from 'react';
import './App.css';
import { GameForm } from './GameForm/EntryPoint';
import { PlayForm } from './Form/EntryPoint';
import { Game, GameTeams, TeamList } from './GameForm/Alies';
import { useForm } from 'react-hook-form';
import { Drive, Play } from './Form/Alies';


function App() {
  const [visible,setVisible] = useState<boolean>(true);
  const [awayTeam,setAwayTeam] = useState<string>("")
  const [homeTeam,setHomeTeam] = useState<string>("")

  console.log(visible)

  const { register ,handleSubmit,watch } = useForm<Play>();

  const [id,setId] = useState<number>(1);
  const [ballOn,setBallOn]=useState<number>(35);

  const [homePoints,setHomePoints] = useState<number>(0);
  const [awayPoints,setAwayPoints] = useState<number>(0);
  const [down,setDown] = useState<number>(1);
  const [distance,setDistance] = useState<number>(10);


  const [yardsDrived,setYardsDrived] = useState<number>(0);
  const [playAmount,setPlayAmount] = useState<number>(0);

  const [gameData,setGameData] = useState<Game[]>([])
  const [driveData,setDriveData] =useState<Drive[]>([])
  const [homeTeamData,setHomeTeamData] = useState<TeamList[]>([])
  const [awayTeamData,setAwayTeamData] = useState<TeamList[]>([])

  useEffect(() => {

      const f = async () =>{
          await fetch("http://localhost:9091/games/latest", {method: 'GET'})
              .then(res => res.json())
              .then(async data => {
                  setGameData(data["Value"])
                  console.log(data["Value"]["awayteam"])
              }).then(() => console.log("fetch gamedata")).then(() => { 
              })
          await fetch("http://localhost:9091/teaminfo/"+homeTeam, {method: 'GET'})
                      .then(res => res.json())
                      .then(async data => {
                          setHomeTeamData(data["Value"])
                      }).then(() => console.log("fetch home"))
          await fetch("http://localhost:9091/teaminfo/"+awayTeam, {method: 'GET'})
                      .then(res => res.json())
                      .then(async data => {
                          setAwayTeamData(data["Value"])
                      }).then(() => console.log("fetch away"))
          await fetch("http://localhost:9091/drives/latest", {method: 'GET'})
              .then(res => res.json())
              .then(async data => {
                  setDriveData(data["Value"])
              }).then(() => console.log("fetch drivedata")).then(() => { 
              })
      }
      f();

  },[])

  const [ballPossession,setBallPossession]=useState<boolean>(gameData[0]?.cointos_result);
  const [ballPlace,setBallPlace]=useState<boolean>(gameData[0]?.cointos_result);

  if(visible){
    return (
      <div className="">
        <GameForm visible={visible} setVisible={setVisible} setAwayTeam={setAwayTeam} setHomeTeam={setHomeTeam}/>
      </div>
    );
  }
  return (
    <div className="">
      <PlayForm visible={visible} homeTeam={homeTeam} awayTeam={awayTeam} register={register} handleSubmit={handleSubmit} watch={watch} id={id} setId={setId} ballOn={ballOn} setBallOn={setBallOn} homePoints={homePoints} setHomePoints={setHomePoints} awayPoints={awayPoints} setAwayPoints={setAwayPoints} down={down} setDown={setDown} distance={distance}setDistance={setDistance} yardsDrived={yardsDrived} setYardsDrived={setYardsDrived} playAmount={playAmount} setPlayAmount={setPlayAmount} gameData={gameData} setGameData={setGameData} driveData={driveData} setDriveData={setDriveData} homeTeamData={homeTeamData} setHomeTeamData={setHomeTeamData} awayTeamData={awayTeamData} setAwayTeamData={setAwayTeamData} ballPossession={ballPossession} setBallPossession={setBallPossession} ballPlace={ballPlace} setBallPlace={setBallPlace}/>
    </div>
  );
}

export default App;
