import React,{useEffect,useState,createContext} from 'react';
import './App.css';
import { Play } from './Alies';
import Pointer from './components/Pointer'

export const FetchData = createContext<Play[]>([])

function App() {

  const [latestPlay,setLatestPlay] = useState<Play[]>([]);


  useEffect(() => {
    fetch('http://localhost:9091/plays/latest', {
      method: 'GET'})
    .then(res => res.json())
    .then(data => {
        setLatestPlay(data["Value"])
        console.log("Rendering!")
    })
  },[])


  return (
    <FetchData.Provider value={latestPlay}>
      <Pointer/>
    </FetchData.Provider>
  );
}

export default App;
